const e=[];function t(e,t,n,c,o,i){this.title=e,this.description=t,this.duedate=n,this.priority=c,this.notes=o,this.checklist=i}t.prototype.checkbtn=function(){this.checklist=!this.checklist},document.querySelector("#newtask").addEventListener("click",(function(){document.querySelector("#newtask-form").style.display=" block"})),document.querySelector("#newtask-form").addEventListener("submit",(function(n){n.preventDefault(),function(){let n=new t(document.querySelector("#title").value,document.querySelector("#description").value,document.querySelector("#duedate").value,document.querySelector("#priority").value,document.querySelector("#notes").value,document.querySelector("#checklist").checked);e.push(n),function(){let t=document.querySelector("#task");t.innerHTML=" ";for(let n=0;n<e.length;n++){let c=e[n],o=document.createElement("div");o.innerHTML=`\n        <div class="card-header">\n        <h3 class="title"> ${c.title}</h3>\n        <h4 class="description">  ${c.description}</h4>\n        </div>\n        <div class="card-body">\n        <p> Notes -- ${c.notes} </p>\n        <p class="done-status"> ${c.checklist?"Done":"Not Done "}</p>\n        <button class="remove-btn" onclick="removeTask(${n})">Remove</button>\n        <button class="done-btn" onclick="doneBtn(${n})">Edit Check</button>\n        </div>\n        `,t.appendChild(o)}}()}()}))();